function getOrdersPerDay(){var a="SELECT * FROM pedidos";mysqlQuery(a,function(a){return a})}function drawChart(a){function e(){var e=new google.visualization.DataTable;e.addColumn("string","Solicitante"),e.addColumn("number","Pedidos");var o=[];$.each(a,function(a,e){var t=[e.nome,e.total];o.push(t)}),e.addRows(o);var t={title:"Quantidade de Pedidos por Solicitante"},i=new google.visualization.BarChart(document.getElementById("chart_div"));i.draw(e,t)}google.charts.setOnLoadCallback(e)}var query="SELECT s.id AS id, s.nome AS nome, COUNT(p.id) AS total FROM pedidos p JOIN solicitantes s ON s.id = p.id_solicitante GROUP BY id";mysqlQuery(query,function(a){console.log(a);var e=JSON.parse(a);drawChart(e)});